import React from "react";
import Table from "./table";
import {Link} from "react-router-dom";

const BASE_URL = "http://localhost:4000/api/actors"

class ActorTable extends Table {
    async getPage(page) {
        const link = (this.props.search) ? "&" : "?";
        const result = await fetch(BASE_URL + this.props.search + link + "page=" + page, {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        });
        const response = await result.json();
        return response["data"]["actors"];
    }

    formatHead() {
        return (
            <th className="text-center">Name</th>
        );
    }

    formatRow({actor_uri, name}) {
        let actorRoute = "/actor-details/" + actor_uri.split("/").slice(-1)[0];
        return (
            <tr key={actor_uri}>
                <td className="text-center">
                    <Link to={actorRoute} className="text-dark">
                        {name}
                    </Link>
                </td>
            </tr>
        );
    }
}

export default ActorTable;
